mapper.select:
	select pool_no as poolNo,
	  pool_name as poolName,
    merchant_no as coreMerchantNo,
    rmk as rmk,
    status as status,
    gmt_created as gmtCreated
	from dep_pool
  where merchant_no=#{coreMerchantNo,jdbcType=VARCHAR}

       and pool_no = #{poolNo,jdbcType=VARCHAR}


       and pool_name = #{poolName,jdbcType=VARCHAR}

  order by gmt_created desc
  limit #{startCount,jdbcType=INTEGER}, #{showCount,jdbcType=INTEGER}

	select count(id)
	from dep_pool
  where merchant_no=#{coreMerchantNo,jdbcType=VARCHAR}

       and pool_no = #{poolNo,jdbcType=VARCHAR}


       and pool_name = #{poolName,jdbcType=VARCHAR}

select pool_no as poolNo, pool_name as poolName, merchant_no as coreMerchantNo, rmk as rmk, status as status, gmt_created as gmtCreated from dep_pool where 1=1
and merchant_no=#{coreMerchantNo,jdbcType=VARCHAR}
and pool_no = #{poolNo,jdbcType=VARCHAR}
and pool_name = #{poolName,jdbcType=VARCHAR}

	select pool_no as poolNo,
	  pool_name as poolName,
    merchant_no as coreMerchantNo,
    rmk as rmk,
    status as status,
    gmt_created as gmtCreated
	  from dep_pool where merchant_no=#{coreMerchantNo,jdbcType=VARCHAR}

       and pool_no = #{poolNo,jdbcType=VARCHAR}


       and pool_name = #{poolName,jdbcType=VARCHAR}

  order by gmt_created desc
  limit #{startCount,jdbcType=INTEGER}, #{showCount,jdbcType=INTEGER}

	select count(id) from dep_pool where merchant_no=#{coreMerchantNo,jdbcType=VARCHAR}

       and pool_no = #{poolNo,jdbcType=VARCHAR}


       and pool_name = #{poolName,jdbcType=VARCHAR}
